	public String getValidateRule(Annotation ruleAnno) {
		return Email.class.getSimpleName();
	}

	public boolean validate(Object... params) {
		String fieldVal = params[0].toString();

		if (BaseUtils.isEmpty(fieldVal)) {
			return true;
		}

		return (PATTERN.matcher(fieldVal).matches() && fieldVal.getBytes().length < EMAIL_MAX_LEN);
	}

